import torch
import torch.nn as nn
import torch.nn.functional as F

#sklearnのデータセット。アイリス（花）の種類を分類
#回帰などの場合はload_diabetes()という糖尿病の進行状況のデータなどを使用する。
#参考https://note.nkmk.me/python-sklearn-datasets-load-fetch/

from sklearn.datasets import load_iris

iris=load_iris()

#入力値と目標値を抽出
x=iris['data']
t=iris['target']

iris

type(x),type(t)

#numpy型からtensor型に変換
x=torch.tensor(x,dtype=torch.float32)
t=torch.tensor(t,dtype=torch.int64)

#<ipython-input-10-34d329a9c3fc>:2: UserWarning:
# テンソルから構造をコピーするには、sourceTensor.clone().detach() または
#sourceTensor.clone().detach().requires_grad_(True) を使用することをお勧めします。

type(x),type(t)

#(torch.Size([150, 4]), torch.Size([150]))と出力される。見方としては、
#torch.Size([150, 4]＝アイリスの種類が150種類で入力値が4種類あるということ。
#入力値は[がく片の長さ,がく片の幅,花びらの長さ,花びらの幅]
x.shape, t.shape

"""DataLoader"""

#モデルに入れるための定義（ミニパッチに必須）

#入力値と目標値をまとめる
dataset = torch.utils.data.TensorDataset(x,t)

len(dataset)

dataset[0]

#データセットの分割

#6:2:2で分ける（学習データ、検証データ、テストデータ）に分ける

#学習データ→モデルのパラメータの最適化
#検証データ→モデルのハイパーパラメータの最適化
#テストデータ→学習済みモデルの評価

#各データのサンプル数を決定
#train:val:test=60%:20%:20%

n_train=int(len(dataset)*0.6)
n_val=int(len(dataset)*0.2)
n_test=len(dataset)-n_train-n_val

n_train, n_val, n_test

#ランダムに分割するため、シードを固定する

torch.manual_seed(0)

#データセットの分割
train,val,test=torch.utils.data.random_split(dataset,[n_train,n_val,n_test])

len(train),len(val),len(test)

"""ミニバッチ学習"""

#バッチサイズの定義
batch_size=10

train_loader=torch.utils.data.DataLoader(train,batch_size,shuffle=True,drop_last=True)
val_loader=torch.utils.data.DataLoader(val,batch_size)
test_loader=torch.utils.data.DataLoader(test,batch_size)

#trainのshuffleは局所解から抜け出すため。
#drop_last=Trueはn_trainのデータ数/バッチサイズで計算した際に余りがあったら切り捨てるため

#train_loaderから1つ目のバッチを取り出す。
#Xを出力した時にdataが10個出力されていることを確認する。バッチサイズが10の為。
#tの出力結果は表示されたｘのデータがどのアヤメ（iris）なのかを出力している。
x,t=next(iter(train_loader))

x

t

"""ネットワークの定義"""

#4→4→3の全結合層を定義

class Net(nn.Module):
  #使用するオブジェクトを定義
  def __init__(self):
    super(Net,self).__init__()
    self.fc1=nn.Linear(4,4)
    self.fc2=nn.Linear(4,3)

#順伝播
  def forward(self,x):
#線形変換、非線形変換を繰り返す
    h=self.fc1(x)
    h=F.relu(h)#relu関数
    h=self.fc2(h)
    return h

#乱数がまた発生するので0に固定
torch.manual_seed(0)
#インスタンス化
net=Net()

net

"""最適化"""

#lrは学習係数
optimizer=torch.optim.SGD(net.parameters(),lr=0.01)

"""学習の流れ![スクリーンショット (343-1).png](data:image/png;base64,)



"""

#バッチをｘとｔに分ける

batch = next(iter(train_loader))

batch

x,t=batch

x

t

#パラメータの確認

#全結合層fc1の重み
print(f'fc1の重み:{net.fc1.weight}')
print('-------------------------------')
#全結合層fc1のバイアス
print(f'fc1のバイアス:{net.fc1.bias}')
print('-------------------------------')
#全結合層fc2の重み
print(f'fc2の重み:{net.fc2.weight}')
print('-------------------------------')
#全結合層fc2のバイアス
print(f'fc2のバイアス:{net.fc2.bias}')

#予測値yを出す
y = net.forward(x)
y

loss= F.cross_entropy(y,t)

loss

#勾配の状況を確認してみる

#全結合層fc1の重みに関する勾配
print(f'fc1の重みに関する勾配:{net.fc1.weight.grad}')
print('------------------------------------------------')
#全結合層fc1のバイアスに関する勾配
print(f'fc1のバイアスに関する勾配{net.fc1.bias.grad}')
print('------------------------------------------------')
#全結合層fc2の重みに関する勾配
print(f'fc2の重みに関する勾配:{net.fc2.weight.grad}')
print('------------------------------------------------')
#全結合層fc2のバイアスに関する勾配
print(f'fc2のバイアスに関する勾配{net.fc2.bias.grad}')

#今の段階ではNoneとなり、格納されていないことがわかる

#backwardを用いて自動微分を行い勾配の計算をする

loss.backward()

#全結合層fc1の重みに関する勾配
print(f'fc1の重みに関する勾配:{net.fc1.weight.grad}')
print('------------------------------------------------')
#全結合層fc1のバイアスに関する勾配
print(f'fc1のバイアスに関する勾配{net.fc1.bias.grad}')
print('------------------------------------------------')
#全結合層fc2の重みに関する勾配
print(f'fc2の重みに関する勾配:{net.fc2.weight.grad}')
print('------------------------------------------------')
#全結合層fc2のバイアスに関する勾配
print(f'fc2のバイアスに関する勾配{net.fc2.bias.grad}')

#パラメータの更新
optimizer.step()

#更新後の値の確認
#全結合層fc1の重み
print(f'fc1の重み:{net.fc1.weight}')
print('-------------------------------')
#全結合層fc1のバイアス
print(f'fc1のバイアス:{net.fc1.bias}')
print('-------------------------------')
#全結合層fc2の重み
print(f'fc2の重み:{net.fc2.weight}')
print('-------------------------------')
#全結合層fc2のバイアス
print(f'fc2のバイアス:{net.fc2.bias}')

#ここまでが一連の流れ

"""指定したデバイスに転送"""

#GPUの有無を確認するコード

torch.cuda.is_available()

#自動的にデバイスの選択の確認※GPUを優先する使えない場合はCPU

device=torch.device('cuda:0'if torch.cuda.is_available()else'cpu')

net.to(device)

x=x.to(device)

t=t.to(device)

#勾配情報の初期化※累積勾配を使わないため

optimizer.zero_grad()

#勾配が初期化されているかの確認
#全結合層fc1の重みに関する勾配
print(f'fc1の重みに関する勾配:{net.fc1.weight.grad}')
print('------------------------------------------------')
#全結合層fc1のバイアスに関する勾配
print(f'fc1のバイアスに関する勾配{net.fc1.bias.grad}')
print('------------------------------------------------')
#全結合層fc2の重みに関する勾配
print(f'fc2の重みに関する勾配:{net.fc2.weight.grad}')
print('------------------------------------------------')
#全結合層fc2のバイアスに関する勾配
print(f'fc2のバイアスに関する勾配{net.fc2.bias.grad}')

#学習のループ
#エポック数
max_epoch=1

torch.manual_seed(0)

#モデルのインスタンス化とdeviceへの転送
net=Net().to(device)

#最適化手法
optimizer=torch.optim.SGD(net.parameters(),lr=0.1)

#学習のループ
for epoch in range(max_epoch):
  for batch in train_loader:
    x,t=batch
    x=x.to(device)
    t=t.to(device)

    #予測値
    y=net(x)
    #損失関数
    loss=F.cross_entropy(y,t)

    print(f'loss:{loss}')
    #勾配を出すために初期化※累積勾配ではないため
    optimizer.zero_grad()
    #勾配を求める
    loss.backward()
    optimizer.step()
    #サンプル数が90個でバッチは10個で1つなので出力結果は9種類出る




    #実装基本の流れ
    #1.バッチサイズ分取り出す
    #2.使用するデバイスに転送する
    #3.予測値を出す
    #4.損失関数算出す
    #5.勾配出す
    #6.パラメーター更新する

"""評価指標の追加"""

#学習の評価（いい学習なのか悪い学習なのか判断）
#今回は正解率で判断

x,t=next(iter(train_loader))
x=x.to(device)
t=t.to(device)
y=net(x)
y

#予測値は一番大きい値のラベルを予測している
#Q.それぞれのサンプルがどのラベルを予測しているのか？
y_label=torch.argmax(y,dim=1)
#torch.argmaxは最大値を求める関数
#ここではdimは「行ごとに」という意味　ディメンションのことを指す
y_label
#出力結果が0の時は左端のデータ、1なら左から2番目、2なら左から3番目

#yとtの一致度を調べたい
y_label==t

#実際、

#y_label==tの総和を求める
(y_label==t).sum()
#出力結果がtensor(7, device='cuda:0')となった場合、7個正解

#正解数を要素数で割れば正解率が出る
#ただ、今の状態だとint型なのでfloat型に変更していく
(y_label==t).sum().float()

#正解率の算出
accuracy = (y_label == t).sum().float() / len(t)
accuracy

#学習ループを書く前に必要なもの

#モデルの初期化
torch.manual_seed(0)

#モデルのインスタンス化とデバイスへの転送
net=Net().to(device)

#最適化手法の選択
optimizer=torch.optim.SGD(net.parameters(),lr=0.1)

#学習ループ
for epoch in range(max_epoch):
  for batch in train_loader:
    x,t=batch
    x=x.to(device)
    t=t.to(device)

    #予測値
    y=net(x)
    #損失関数
    loss=F.cross_entropy(y,t)
    #正解率追加
    y_label=torch.argmax(y,dim=1)
    accuracy=(y_label==t).sum() .float ()/len(t)
    print(f'accuracy:{accuracy:.2f}')#.2fは小数点の数の指定
    #勾配を出すために初期化※累積勾配ではないため
    optimizer.zero_grad()
    #勾配を求める
    loss.backward()
    optimizer.step()
    #サンプル数が90個でバッチは10個で1つなので出力結果は9種類出る




    #実装基本の流れ
    #1.バッチサイズ分取り出す
    #2.使用するデバイスに転送する
    #3.予測値を出す
    #4.損失関数算出す
    #5.勾配出す
    #6.パラメーター更新する

"""検証データとテストデータ"""

#正解率を計算する関数
def clac__accracy(data_loader):
  with torch.no_grad():
    total=0
    correct=0.0

    for batch in data_loader:
      x,t=batch
      x=x.to(device)
      t=t.to(device)
      y=net(x)

      y_label=torch.argmax(y,dim=1)
      total +=len(t)
      correct += (y_label==t).sum()

      #全体の平均を算出
      accuracy=correct/total

    return accuracy

clac__accracy(val_loader)#引数に検証データを入れて検証データの正解率を求める

clac__accracy(test_loader)#引数にテストデータを入れて検証データの正解率を求める

